vulpejs.ng.app.controller("ResetPasswordController",["$rootScope","$scope","VulpeJS",function(a,c,b){var d=new b().init(c);d.changed=false;d.error=false;d.item={id:"",email:"",password:"",confirmPassword:""};d.reset=function(){if(c.resetPasswordForm.$valid){var e=d.item;d.message.clean();d.http.post({url:"/reset-password",data:{id:e.id,email:e.email,password:e.password},callback:{success:function(){d.changed=true},error:function(h,f,i,g){d.error=true}}})}else{if(!d.item.password){$("#userPassword").focus()}else{if(!d.item.passwordConfirm){$("#userPasswordConfirm").focus()}}}};d.on.ready(function(){d.item.id=$("#token").val();d.item.email=$("#email").val()})}]);